mapper.select:
SELECT a.userid AS userId,a.username AS userName,GROUP_CONCAT(r.rolename) AS roleName FROM (SELECT u.userid,u.username,us.roleid FROM `user` u LEFT JOIN userrolerelationship us On u.userid=us.userid WHERE NOT us.roleid=1) a LEFT JOIN role r ON a.roleid=r.roleid GROUP BY a.userid
userName like concat('%', #{userName}, '%')
order by a.userId limit #{pageIndex},#{pageSize}
