mapper.select:
SELECT s.stuId,s.stuName,s.sex,s.nation,s.birthday,s.birthPlace,s.marry, s.telephone,s.idCard,s.university,s.major,s.photo,s.note,s.state, s.classId,s.deptId,cl.className,d.deptName FROM student AS s INNER JOIN class AS cl ON s.classId = cl.classId INNER JOIN dept AS d ON s.deptId = d.deptId
s.stuName like concat('%',#{stuName},'%')
and cl.classId = #{classId}
and d.deptId = #{deptId}

SELECT s.stuId,s.stuName,s.sex,s.nation,s.birthday,s.birthPlace,s.marry, s.telephone,s.idCard,s.university,s.major,s.photo,s.note,s.state, s.classId,s.deptId,cl.className,d.deptName FROM student AS s INNER JOIN class AS cl ON s.classId = cl.classId INNER JOIN dept AS d ON s.deptId = d.deptId
s.stuName like concat('%',#{stuName},'%')
and cl.classId = #{classId}
and d.deptId = #{deptId}
order by s.stuId limit #{pageIndex}, #{pageSize}
