mapper.update:
UPDATE followsystem.user
SET loginTime=#{loginTime}
WHERE userId=#{userId}

mapper.select:
SELECT a.userId,a.userName,a.pwd,a.phone,a.email,a.note,a.flag,DATE_FORMAT(a.loginTime,'%Y-%m-%d %H:%i:%S') loginTime,d.deptName FROM (SELECT userId,userName,pwd,phone,email,note,flag,loginTime,deptid FROM `user` WHERE userId IN ( SELECT userId FROM userrolerelationship WHERE roleid != 1 )) a LEFT JOIN dept d ON a.deptId=d.deptId GROUP BY a.userId
a.userName like concat('%', #{userName}, '%')
and a.deptId = #{deptId}

SELECT a.userId,a.userName,a.pwd,a.phone,a.email,a.note,a.flag,DATE_FORMAT(a.loginTime,'%Y-%m-%d %H:%i:%S') loginTime,d.deptName FROM (SELECT userId,userName,pwd,phone,email,note,flag,loginTime,deptid FROM `user` WHERE userId IN ( SELECT userId FROM userrolerelationship WHERE roleid != 1 )) a LEFT JOIN dept d ON a.deptid=d.deptid GROUP BY a.userId
a.userName like concat('%', #{userName}, '%')
and a.deptId=#{deptId}
order by a.userId limit #{pageIndex},#{pageSize}

mapper.update:
UPDATE followsystem.user
SET userName=#{userName}
,pwd=#{pwd}
,phone=#{phone}
,email=#{email}
,note=#{note}
,deptId=#{deptId}
WHERE userId=#{userId}

mapper.select:
SELECT a.userid,a.userName,a.pwd,r.roleName FROM (SELECT u.userId,u.userName,u.pwd,us.roleid FROM `user`u LEFT JOIN userrolerelationship us ON u.userid=us.userid WHERE NOT us.roleid = 1) a LEFT JOIN role r ON a.roleid=r.roleid GROUP BY a.userid
a.userName like concat('%', #{userName}, '%')
and a.roleId=#{roleId}
order by a.userId limit #{pageIndex},#{pageSize}
